import { useState } from "react";
import axios from "axios";
import "./Remedies.css";

const categories = [
  {
    key: "cold and cough",
    title: "Cold & Cough",
    image: "data:image/jpeg;base64,"
  },
  {
    key: "indigestion",
    title: "Indigestion",
    image: "data:image/jpeg;base64,"
  },
  {
    key: "fever",
    title: "Fever",
    image: "data:image/jpeg;base64,"
  },
  {
    key: "headache",
    title: "Headache",
    image: "data:image/jpeg;base64,"
  },
  {
    key: "skin",
    title: "Skin Problems",
    image: "data:image/jpeg;base64,"
  },
  {
    key: "immunity",
    title: "Immunity Boost",
    image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRcTWz5OZJ2ODO3oPR1b5Wq9RcSvRxxXHOlOA&s"
  }
];


export default function Remedies() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [remedies, setRemedies] = useState([]);
  const [symptom, setSymptom] = useState("");
  const [aiResult, setAiResult] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchRemedies = async (category) => {
    try {
      setSelectedCategory(category);
      const res = await axios.get(
        `http://localhost:5000/api/remedies?category=${category}`
      );
      setRemedies(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const getAISuggestion = async () => {
    if (!symptom.trim()) return;

    try {
      setLoading(true);
      setAiResult("");

      const res = await axios.post(
        "http://localhost:5000/api/ai/remedy",
        { symptom }
      );

      setAiResult(res.data.result);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="remedies-page">
      <h1 className="page-title">Home Remedies</h1>

      {/* CATEGORY CARDS */}
      {!selectedCategory && (
        <div className="category-grid">
          {categories.map((c) => (
            <div
              key={c.key}
              className="category-card"
              onClick={() => fetchRemedies(c.key)}
            >
              {/* ‚úÖ FIXED HERE */}
              <img src={c.image} alt={c.title} loading="lazy" />
              <h3>{c.title}</h3>
            </div>
          ))}
        </div>
      )}

      {/* REMEDY LIST */}
      {selectedCategory && (
        <>
          <button className="back-btn" onClick={() => setSelectedCategory(null)}>
            ‚Üê Back
          </button>

          <h2 className="category-title">{selectedCategory}</h2>

          <div className="remedy-grid">
            {remedies.map((r) => (
              <div key={r._id} className="remedy-card">
                <h3>{r.title}</h3>
                <p><b>Plant:</b> {r.plant}</p>
                <p><b>Part Used:</b> {r.partUsed}</p>
                <p><b>Ingredients:</b> {r.ingredients.join(", ")}</p>
                <p><b>Preparation:</b> {r.preparation}</p>
                <p><b>Dosage:</b> {r.dosage}</p>
                <p className="caution">‚ö† {r.caution}</p>
              </div>
            ))}
          </div>

          {/* AI SUGGESTION */}
          <div className="ai-box">
            <h3>ü§ñ Expert AI Suggestion</h3>

            <input
              type="text"
              placeholder="Enter your symptom (eg: dry cough)"
              value={symptom}
              onChange={(e) => setSymptom(e.target.value)}
            />

            <button onClick={getAISuggestion} disabled={loading}>
              {loading ? "Analyzing..." : "Get AI Remedy"}
            </button>

            {aiResult && <pre className="ai-result">{aiResult}</pre>}
          </div>
        </>
      )}
    </div>
  );
}